package adapter;

// static 형태로 개발하여 인스턴스화 없이 사용가능
// 라이브러리 형태의 개발

public class Math {
	public static double twoTime(double num) {
		// TODO Auto-generated method stub
		return num*2;
	}
	
	public static double half(double num) {
		// TODO Auto-generated method stub
		return num/2;
	}
	
	public static Double doubled(Double d) {
		// TODO Auto-generated method stub
		return d*2;
	}
}
